import React, { useState } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

const Home = () => {

  const [showLoginForm, setShowLoginForm] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLoginButtonClick = () => {
    setShowLoginForm(true);
  };

  const handleFormSubmit = () => {
    // Handle form submission logic here (e.g., authentication)
    // For this example, let's assume the form is valid and the user can proceed
    console.log('Form submitted successfully!');
  };
  return (
    <div className='home'>
      <div className='card'>
      <div className="login-form-container">
      {!showLoginForm ? (
        <button onClick={handleLoginButtonClick} className="login-button">
          Login
        </button>
      ) : (
        <div className="form-fields">
        <div>
          <img className='charimg' src="charusat.png" alt="charimg" />
        </div>
        <div className="input-group flex-nowrap">
        <i className='fa fa-user icon' style={{marginTop:'11px'}}></i>
          <input
            type="text"
            class="form-control"
            aria-label="Username" aria-describedby="addon-wrapping"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{marginBottom:'20px', border:'none',borderRadius:'5px'}}
          />
          </div>
          <div className="input-group flex-nowrap">
          <span class="input-group-text" id="addon-wrapping"><i className='fa fa-lock icon'></i></span>
          <input
            type="password"
            class="form-control"
            aria-label="Password" aria-describedby="addon-wrapping"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{marginBottom:'20px', border:'none',borderRadius:'5px'}}
          />
          </div>
          <Link to="/dashboard"><button onClick={handleFormSubmit} className="submit-button">
            Submit
          </button></Link>
        </div>
      )}
    </div>
      {/* <Link to="/login"><button className='loginbtn'>Login</button></Link> */}
      {/* <Link to="/register"><button className='registerbtn'>Register as</button></Link> */}
      </div>
    </div>
  );
};

export default Home;
