import React, { useState } from 'react';
import './Register.css';
// import { Form } from 'react-router-dom';

const Register = () => {
  const [username, setUsername] = useState('');

  // Replace 'example.com' with your desired domain
  const domain = 'charusat.edu.in';

  const handleInputChange = (event) => {
    setUsername(event.target.value);
  };
  return (
    <div id='register' className='register'>
      <div className='card'>
      <form>
        <div className='row'>
        <div className='col-5'>
        <label className='labelfull'>Full Name:
        <input type='text' className='fullname'></input>
        </label>
        </div>
        <div className='col-5'>
        <label className='labellast'>Last Name:
        <input type='text' className='lastname'></input>
        </label>
        </div>
        </div>
        <br />
        <div className='row'>
        <div className='col-5'>
        <label htmlFor='username' className='labelemail'>Email:
        <input type="email" className='email' name="email"></input>
        </label>
        </div>
        <div className='col-5'>
        <label className='labelemail'>Email:
        <input type="text"
        id="username"
        value={username}
        onChange={handleInputChange} className='email'></input>
        <p>Email: {username}@{domain}</p>
        </label>
        </div>
        </div>
      </form>
      </div>
      </div>
  )
}

export default Register
